import React, { useState } from 'react';


// Prodcutos Carrito de Compras
const productos = [
    { id: 1, nombre: 'Omelette', precio: '25.000', imagen: "data:image/jpeg;base64,"},
    { id: 2, nombre: 'combo jugo de naranja ', precio: '19.000', imagen: "data:image/jpeg;base64,"},
    { id: 3, nombre: 'Tocineta variedad', precio: '20.000', imagen: "data:image/jpeg;base64,"},
    { id: 4, nombre: 'Huevos al gusto', precio: '25.000', imagen: "data:image/jpeg;base64,"},
];


const Productos = ({ carrito, setCarrito }) => {
    const agregarAlCarrito = (producto) => {
        const existe = carrito.find((item) => item.id === producto.id);
        if (existe) {
            alert(`${producto.nombre} ya está en el carrito.`);
        } else {
            setCarrito([...carrito, producto]);
            alert(`${producto.nombre} ha sido agregado al carrito.`);
        }
    };

    return (
        <div className="productos-container">
            <h1>Productos Disponibles</h1>
            <div className="productos-grid">
                {productos.map((producto) => (
                    <div className="producto-card" key={producto.id}>
                        <img src={producto.imagen} alt={producto.nombre} />
                        <h2>{producto.nombre}</h2>
                        <p>Precio: ${producto.precio}</p>
                        <button onClick={() => agregarAlCarrito(producto)}>Agregar al Carrito</button>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Productos;